mapper.select:
SELECT 

FROM fs_good a

WHERE a.id = #{id}

		SELECT 

		FROM fs_good a


			a.del_flag = #{DEL_FLAG_NORMAL}

				AND a.name LIKE 
					'%'||#{name}||'%'
					'%'+#{name}+'%'
					concat('%',#{name},'%')


				AND a.brand like
                '%'||#{brand}||'%'
                '%'+#{brand}+'%'
                concat('%',#{brand},'%')


				AND a.itemnumber = #{itemnumber}


				AND a.unitnum = #{unitnum}


				AND a.unit = #{unit}


				AND a.factory = #{factory}


				AND a.function = #{function}


				AND a.description = #{description}


				AND a.cooperation = #{fsGoodCooperation.id}




				ORDER BY ${page.orderBy}


				ORDER BY a.update_date DESC

SELECT 

FROM fs_good a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE fs_good SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
