mapper.select:
SELECT 

FROM fs_material_warehouseinfo a

WHERE a.id = #{id}

SELECT
FROM fs_material_warehouseinfo a
a.del_flag = #{DEL_FLAG_NORMAL}
AND a.parent_id = #{parent.id}
AND a.parent_ids LIKE '%'||#{parentIds}||'%' '%'+#{parentIds}+'%' concat('%',#{parentIds},'%')
AND a.name LIKE '%'||#{name}||'%' '%'+#{name}+'%' concat('%',#{name},'%')
AND a.warehouse_pos LIKE '%'||#{warehousePos}||'%' '%'+#{warehousePos}+'%' concat('%',#{warehousePos},'%')
AND a.type = #{type}
AND a.status = #{status}
ORDER BY a.sort ASC

SELECT a.id, a.parent_id AS "parent.id", a.parent_ids FROM fs_material_warehouseinfo a
a.del_flag = #{DEL_FLAG_NORMAL} AND a.parent_ids LIKE #{parentIds}
ORDER BY a.sort ASC

mapper.update:
UPDATE fs_material_warehouseinfo SET 
	parent_id = #{parent.id}, 
	parent_ids = #{parentIds}
WHERE id = #{id}

UPDATE fs_material_warehouseinfo SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id} OR parent_ids LIKE '%,'||#{id}||',%'
